import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { lugarModel } from 'src/app/models/lugarModel';
import { lugarService } from 'src/app/services/lugar.service';
import { ReactiveFormsModule } from '@angular/forms'; // Importa ReactiveFormsModule
@Component({
  selector: 'app-lugar',
  templateUrl: './lugar.component.html',
  styleUrls: ['./lugar.component.css']
})
export class LugarComponent implements OnInit{
  lugar: lugarModel[] = [];
  lugarForm: FormGroup;

  constructor(private lugarService: lugarService, private fb: FormBuilder) {
    this.lugarForm = this.fb.group({
      _id: [''],
      nombrePais: ["", Validators.required],
      nombreEstado: ["", Validators.required],
      nombreCiudad: ["", Validators.required],
    });
  }

  ngOnInit() {
    this.cargarLugar();
  }

  cargarLugar() {
    this.lugarService.getLugar().subscribe(
      data => {
        this.lugar = data;
      },
      error => {
        console.error('Error al cargar Lugar:', error);
      }
    );
  }

  agregarLugar() {
    if (this.lugarForm.valid) {
      this.lugarService.addLugar(this.lugarForm.value).subscribe(
        () => {
          this.cargarLugar();
          this.lugarForm.reset();
        },
        error => {
          console.error('Error al agregar Lugar:', error);
        }
      );
    }
  }

  // actualizarLugar() {
  //   if (this.lugarForm.valid) {
  //     this.lugarService.updateLugar(this.lugarForm.value).subscribe(
  //       () => {
  //         this.cargarLugar();
  //         this.lugarForm.reset();
  //       },
  //       error => {
  //         console.error('Error al actualizar Datos Personales:', error);
  //       }
  //     );
  //   }
  // }

  eliminarPais(id: string | undefined) {
    if (id) {
      this.lugarService.deleteLugar(id).subscribe(
        data => {
          console.log('Lugar eliminado:', data);
          this.cargarLugar();
        },
        error => {
          console.error('Error al eliminar Lugar:', error);
        }
      );
    }
  }

  editarLugar(lugar: lugarModel) {
    this.lugarForm.patchValue(lugar);
  }
}
