import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms'; // Importa FormBuilder y Validators


import { ReactiveFormsModule } from '@angular/forms'; // Importa ReactiveFormsModule
//servicios
import { DatosPService } from 'src/app/services/datos-pservice.service';
//modelos
import { DatosPModel } from 'src/app/models/datosPModel';
import { paisModel } from 'src/app/models/paisModel';
import { CiudadModel } from 'src/app/models/ciudadModel';
import { EstadoModel } from 'src/app/models/estadoModel';
import { EstadoService } from 'src/app/services/estado.service';
import { CiudadService } from 'src/app/services/ciudad.service';

@Component({
  selector: 'app-datos-p',
  templateUrl: './datos-p.component.html',
  styleUrls: ['./datos-p.component.css']
})
export class DatosPComponent implements OnInit {
  datosP: DatosPModel[] = [];
  paises: paisModel[] = [];
  ciudad: CiudadModel[]=[];
  estado: EstadoModel[]=[];

  datosPaises: DatosPModel[] = [];

  datosPFiltrados: DatosPModel[] = []; 
  datosPForm: FormGroup;
  filtroEstado: string = '';
  filtroCiudad: string = '';
  filtroPais: string = '';
  filtroSexo: string = '';


  constructor(private DatosPService: DatosPService, 
              private estadoService: EstadoService,
              private ciudadService: CiudadService,
    private fb: FormBuilder) {
    this.datosPForm = this.fb.group({
      _id: [''],
      telefono: [0, Validators.required],
      dateNac: [0, Validators.required],
      sexo: ['', Validators.required], 
      cp: [0, Validators.required], 
      nombrePais: ['', Validators.required], 
      nombreEstado: ['', Validators.required], 
      nombreCiudad: ['', Validators.required] 
    });
  }

  ngOnInit() {
    this.cargarDatosP();
    this.cargarPaises();
    this.cargarCiudades('');
    this.cargarEstados(''); // Carga los estados

  }

  cargarDatosP() {
    this.DatosPService.getDatosP().subscribe(
      data => {
        this.datosP = data;
      },
      error => {
        console.error('Error al cargar datos Personales:', error);
      }
    );
  }

  agregarDatosP() {
    if (this.datosPForm.valid) {
      this.DatosPService.addDatosP(this.datosPForm.value).subscribe(
        () => {
          this.cargarDatosP();
          this.datosPForm.reset();
        },
        error => {
          console.error('Error al agregar Datos Personales:', error);
        }
      );
    }
  }

  actualizarDatosP() {
    if (this.datosPForm.valid) {
      this.DatosPService.updateDatosP(this.datosPForm.value).subscribe(
        () => {
          this.cargarDatosP();
          this.datosPForm.reset();
        },
        error => {
          console.error('Error al actualizar Datos Personales:', error);
        }
      );
    }
  }

  eliminarDatosP(id: string | undefined) {
    if (id) {
      this.DatosPService.deleteDatosP(id).subscribe(
        data => {
          console.log('Ciudad eliminada:', data);
          this.cargarDatosP();
        },
        error => {
          console.error('Error al eliminar ciudad:', error);
        }
      );
    }
  }

  editarDatosP(datosP: DatosPModel) {
    this.datosPForm.patchValue(datosP);
  }
  filtrarDatos(event: any) {
    const filtro = event.target.value;
    this.DatosPService.getDatosP().subscribe(
      data => {
        if (filtro === '') {
          // Mostrar todos los datos si no se proporciona ningún filtro
          this.datosP = data;
        } else {
          // Aplicar filtros según los criterios proporcionados
          this.datosP = data.filter(item =>
            (item.nombreEstado.toLowerCase().includes(this.filtroEstado.toLowerCase()) || this.filtroEstado === '') &&
            (item.nombreCiudad.toLowerCase().includes(this.filtroCiudad.toLowerCase()) || this.filtroCiudad === '') &&
            (item.nombrePais.toLowerCase().includes(filtro.toLowerCase()) || filtro === '') && // Utiliza el valor del input como filtro de país
            (item.sexo.toLowerCase().includes(this.filtroSexo.toLowerCase()) || this.filtroSexo === '')
          );
        }
      },
      error => {
        console.error('Error al cargar datos Personales:', error);
      }
    );
  }
  //filtrado
  cargarPaises(): void {
    this.DatosPService.getDatosP().subscribe(
      data => {
        this.paises = data; // Cambia 'datosPaises' a 'paises'
        console.log('Datos de países cargados:', this.paises); // Cambia 'datosPaises' a 'paises'
      },
      error => {
        console.error('Error al cargar datos de países:', error);
      }
    );
  }

  filtrarEstadosPorPais(event: any) {
    const nombrePais = event.target.value;
    if (nombrePais) {
      // Filtrar los estados según el país seleccionado
      this.estadoService.getEstado().subscribe(
        data => {
          this.estado = data.filter(estado => estado.nombrePais === nombrePais); // Cambia 'ciudad' a 'estado' y 'pais' a 'nombrePais'
          // Cargar las ciudades correspondientes al país seleccionado
          this.cargarCiudades(nombrePais);
        },
        error => {
          console.error('Error al cargar estados por país:', error);
        }
      );
    } else {
      // Si no se selecciona ningún país, cargar todos los estados y todas las ciudades
      this.cargarCiudades('');
    }
  }

  filtrarEstadosPorCiudad(event: any) {
    const nombreCiudad = event.target.value;
    if (nombreCiudad) {
      // Filtrar los estados según la ciudad seleccionada
      this.estadoService.getEstado().subscribe(
        data => {
          this.estado = data.filter(estado => estado.nombreEstado === nombreCiudad); // Cambia 'ciudad' a 'estado' y 'nombre' a 'nombreCiudad'
          // Actualizar la lista de ciudades
          this.ciudad = this.ciudad.filter(ciudad => ciudad.nombreCiudad === nombreCiudad); // Cambia 'ciudades' a 'ciudad' y 'nombre' a 'nombreCiudad'
        },
        error => {
          console.error('Error al cargar estados por ciudad:', error);
        }
      );
    } else {
      // Si no se selecciona ninguna ciudad, cargar todos los estados
      this.cargarEstados('');
    }
  }

  cargarCiudades(paisSeleccionado: string) {
    if (paisSeleccionado) {
      // Obtener las ciudades del país seleccionado
      this.ciudadService.getCiudadesPorPais(paisSeleccionado).subscribe(
        data => {
          this.ciudad = data;
          console.log('Ciudades cargadas para', paisSeleccionado + ':', this.ciudad);
        },
        error => {
          console.error('Error al cargar ciudades:', error);
        }
      );
    } else {
      // Si no se selecciona ningún país, cargar todas las ciudades
      this.ciudadService.getCiudad().subscribe(
        data => {
          this.ciudad = data;
          console.log('Todas las ciudades cargadas:', this.ciudad);
        },
        error => {
          console.error('Error al cargar ciudades:', error);
        }
      );
    }
  }

  cargarEstados(nombreCiudad: string) {
    if (nombreCiudad) {
      // Obtener los estados de la ciudad seleccionada
      this.ciudadService.getEstadosPorCiudad(nombreCiudad).subscribe(
        data => {
          this.estado = data;
        },
        error => {
          console.error('Error al cargar estados por ciudad:', error);
        }
      );
    } else {
      // Si no se proporciona ninguna ciudad, cargar todos los estados
      this.estadoService.getEstado().subscribe(
        data => {
          this.estado = data;
          console.log('Estados cargados:', this.estado);
        },
        error => {
          console.error('Error al cargar estados:', error);
        }
      );
    }
  }



