import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PerfilModel } from 'src/app/models/perfilModel';
import { PerfilAdminService } from 'src/app/services/perfil-admin.service';

@Component({
  selector: 'app-perfil-admin',
  templateUrl: './perfil-admin.component.html',
  styleUrls: ['./perfil-admin.component.css']
})
export class PerfilAdminComponent implements OnInit {
  perfil: PerfilModel[] = [];
  perfilForm: FormGroup;

  constructor(private perfilService: PerfilAdminService, private fb: FormBuilder) {
    this.perfilForm = this.fb.group({
      _id: [''],
      puesto: ["", Validators.required],
      habilidades: [[], Validators.required], // Cambio para recibir un array
      Sos: [[], Validators.required], // Cambio para recibir un array
      experiencia: [0, Validators.required],
      idiomas: [[], Validators.required], // Cambio para recibir un array
      certificaciones: [[]], // Cambio para recibir un array vacío
    });
  }

  ngOnInit() {
    this.cargarPerfil();
  }

  cargarPerfil() {
    this.perfilService.getPerfil().subscribe(
      data => {
        this.perfil = data;
      },
      error => {
        console.error('Error al cargar datos Personales:', error);
      }
    );
  }

  agregarPerfil() {
    if (this.perfilForm.valid) {
      this.perfilService.addPerfil(this.perfilForm.value).subscribe(
        () => {
          this.cargarPerfil();
          this.perfilForm.reset();
        },
        error => {
          console.error('Error al agregar perfil', error);
        }
      );
    }
  }

  actualizarPerfil() {
    if (this.perfilForm.valid) {
      this.perfilService.updatePerfil(this.perfilForm.value).subscribe(
        () => {
          this.cargarPerfil();
          this.perfilForm.reset();
        },
        error => {
          console.error('Error al actualizar perfil:', error);
        }
      );
    }
  }

  eliminarPerfil(id: string | undefined) {
    if (id) {
      this.perfilService.deletePerfil(id).subscribe(
        data => {
          console.log('Perfil eliminado:', data);
          this.cargarPerfil();
        },
        error => {
          console.error('Error al eliminar Perfil:', error);
        }
      );
    }
  }

  editarPerfil(perfil: PerfilModel) {
    this.perfilForm.patchValue(perfil);
  }
}
<div class="container">
    <br>
    <div class="row">
      <!-- Parte Izquierda - Formulario -->
      <div class="col-md-4">
        <div class="card">
          <div class="card-body">
            <h5 class="card-title" class="text-dart">Agregar/Editar Paises</h5>

            <form [formGroup]="perfilForm">
              <div class="form-group">
                <label for="puesto" class="text-dart">Puesto</label>
                <input type="text"  class="form-control" id="puesto" name="puesto" formControlName="puesto" required>
              </div>
              <div class="form-group">
                <label for="habilidades" class="text-dart">Lenguajes de Programación</label>
                <input type="text"  class="form-control" id="habilidades" name="habilidades" formControlName="habilidades" required>
              </div>
              <div class="form-group">
                <label for="Sos" class="text-dart">Sistemas Operativos</label>
                <input type="text"  class="form-control" id="Sos" name="Sos" formControlName="Sos" required>
              </div>
              <div class="form-group">
                <label for="experiencia" class="text-dart">Años de Experiencia</label>
                <input type="text"  class="form-control" id="experiencia" name="experiencia" formControlName="experiencia" required>
              </div>
              <div class="form-group">
                <label for="idiomas" class="text-dart">Idiomas</label>
                <input type="text"  class="form-control" id="idiomas" name="idiomas" formControlName="idiomas" required>
              </div>
              <div class="form-group">
                <label for="certificaciones" class="text-dart">Certificaciones</label>
                <input type="text"  class="form-control" id="certificaciones" name="certificaciones" formControlName="certificaciones" required>
              </div>
             
              <div class="btn-group mt-2" >
                <button type="button" class="btn btn-primary" (click)="agregarPerfil()">Agregar</button>
                <button type="button" class="btn btn-success ms-2" (click)="actualizarPerfil()">Actualizar</button>
              </div>
            </form>
            
          </div>
        </div>
      </div>
  
      <!-- Parte Derecha - Tabla -->
      <div class="col-md-8">
        <table class="table">
            <thead>
              <tr class="table-primary">
                <th scope="col">Puesto Solicitado</th>
                <th scope="col">Lenguajes de Programación</th>
                <th scope="col">Sistemas Operativos</th>
                <th scope="col">Experiencia</th>
                <th scope="col">Idiomas</th>
                <th scope="col">Certificaciones</th>
                <th scope="col">Editar</th>
                <th scope="col">Eliminar</th>
              </tr>
            </thead>
            <tbody id="perfil-table-body">
              <tr *ngFor="let item of perfil">
                <td>{{ item.puesto }}</td>
                <td>{{ item.habilidades.join(', ') }}</td>
                <td>{{ item.Sos.join(', ') }}</td>
                <td>{{ item.experiencia }}</td>
                <td>
                  <ul>
                    <li *ngFor="let idioma of item.idiomas">{{ idioma }}</li>
                  </ul>
                </td>
                <td>
                  <ul>
                    <li *ngFor="let certificacion of item.certificaciones">{{ certificacion }}</li>
                  </ul>
                </td>
                <td>
                  <button class="btn btn-sm ms-2 btn-primary" (click)="editarPerfil(item)"><i class="fa fa-edit"></i></button>
                </td>
                <td>
                  <button type="button" class="btn btn-danger btn-sm ms-2" (click)="eliminarPerfil(item._id)"><i class="fa fa-trash"></i></button>
                </td>
              </tr>
            </tbody>
          </table>
      </div>
    </div>
  </div>
  
  <script src="https://code.jquery.com/jquery-3.5.1.slim.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.10.2/dist/umd/popper.min.js"></script>
  <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootswatch@4.5.2/dist/flatly/bootstrap.min.css" integrity="sha384-qF/QmIAj5ZaYFAeQcrQ6bfVMAh4zZlrGwTPY7T/M+iTTLJqJBJjwwnsE5Y0mV7QK" crossorigin="anonymous">
