import { Component, OnInit } from '@angular/core';
import { FormsModule } from '@angular/forms'; // Asegúrate de importar FormsModule
import { DatosPModel } from 'src/app/models/datosPModel';
import { DatosPService } from 'src/app/services/datos-pservice.service';

@Component({
  selector: 'app-datos-p',
  templateUrl: './datos-p.component.html',
  styleUrls: ['./datos-p.component.css']
})
export class DatosPComponent implements OnInit {

  datosP: DatosPModel[] = [];
  telefono: number = 0;
  dateNac: Date; // Se define como Date o null
  sexo: string = "";
  cp: number = 0;
  pais: string = "";
  estado: string = "";
  ciudad: string = "";
  isDone: boolean = false;

  constructor(private datosPService: DatosPService) {
    this.dateNac = new Date();
  }

  ngOnInit() {
    this.datosPService.getDatosP()
      .subscribe(datosP => {
        this.datosP = datosP;
      });
  }

  addDatosP() {
    const newDatosP: DatosPModel = {
      telefono: this.telefono,
      dateNac: new Date(this.dateNac),
      sexo: this.sexo,
      cp: this.cp,
      pais: this.pais,
      estado: this.estado,
      ciudad: this.ciudad,
      isDone: false,
      user: 0
    };

    this.datosPService.addDatosP(newDatosP).subscribe(response => {
      // Si el servicio devuelve los datos agregados, puedes manejarlos aquí
      this.datosP.push(response); // Suponiendo que response contiene los datos agregados
    });
  }
}
<div class="container">
    <div class="row">
        <div class="col-md-7">
            <p>datos-p works!</p>
        </div>
    </div>
</div>

<div class="card">
    <div class="card-body">
        <form (submit)="addDatosP()">
            <div class="input-group">
                <input type="number" name="telefono" {{ngModel}}="telefono" placeholder="Ingresa tu Número de Teléfono" class="form-control">
                <span class="input-group-addon">
                    <button type="submit" class="btn btn-primary">Enviar</button>
                </span>
            </div>
        </form>
    </div>
</div>

<table class="table">
    <thead>
        <tr>
            <th>Teléfono</th>
            <th>Fecha De Nacimiento</th>
            <th>Sexo</th>
            <th>Código Postal</th>
            <th>País</th>
            <th>Ciudad</th>
            <th>Estado</th>
        </tr>  
    </thead>
    <tbody>
        <tr *ngFor="let dato of datosP"> <!-- Corregido aquí -->
         <td>
             {{dato.telefono}}
         </td>
         <td>
             {{dato.telefono}}
         </td>
         <td>
             {{dato.dateNac}}
         </td>
         <td>
             {{dato.sexo}}
         </td>
         <td>
             {{dato.cp}}
         </td>
         <td>
             {{dato.pais}}
         </td>
         <td>
             {{dato.estado}}
         </td>
         <td>
             <button class="btn btn-danger">
                 delete
             </button>
         </td>
        </tr>
     </tbody>

</table>
